package Prac1;

public class Sum2NosWithoutAnyOperator {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int a=5;
		int b=4;
		
		for(int i=0;i<b;i++)
		{
			++a;
		}
		
		System.out.println(a);
		
		//if 2nd no is negative
		int d=-9;
		int c=-4;
		while(c<0)
		{
			d--;
			c++;
		}
		
		System.out.println(d);

	}

}
